  )
values (#{dtId,jdbcType=DECIMAL}, #{dtChannelCode,jdbcType=VARCHAR}, #{dtLastDate,jdbcType=VARCHAR}, 
  #{dtCurrentDate,jdbcType=VARCHAR}, #{dtNextDate,jdbcType=VARCHAR}, #{dtCtime,jdbcType=TIMESTAMP}, 
  #{dtUtime,jdbcType=TIMESTAMP}, #{dtCuser,jdbcType=VARCHAR}, #{dtUuser,jdbcType=VARCHAR}
  )

